import React, {useState, useEffect, useRef} from "react";
import {AddCircle, CreditCard, Gif, EmojiEmotions} from '@material-ui/icons';
import ChatHeader from "./ChatHeader";
import app from "../firebaseConfig";
import {getFirestore, doc, setDoc} from "firebase/firestore";
const firestore1 = getFirestore(firebase);
function ChatScreen({Activechat}){


    const {inputMensaje, setInputMensaje} = useState("");

    function sendMessage(e){
        e.preventDefault();
        const docuRef =doc(firestore1,`chat/${Activechat}/mensajes/${new Date().getTime()}`)
        setDoc(docuRef,{
            mensajes:inputMensaje,
            usuarios: "Ignacio"
        })

        setInputMensaje("");
    }
    
    return( <div className="chat">

        <ChatHeader />

        <div className="chat_messages">
            {/*Mappear mensajes de firebases*/}
        </div>
        
        <div className="chat_input">
           
            <form onSubmit={sendMessage}>
                <input type="text" disabled={false} value={inputMensaje} 
                onChange={(e)=>setInputMensaje(e.target.value)} 
                placeholder="Type your message"
                />

                <button disabled={false} value={inputMensaje} clasName="chat_inputButton" type="submit">
                    Send Message
                </button>

            </form>

            <div className="chat_inputIcons">
                
            </div>

        </div>
    
    </div>
    )

}

export default ChatScreen;
