import React, {useState, useEffect, useRef} from "react";
import {AddCircle, CreditCard, Gif, EmojiEmotions} from '@material-ui/icons';
import './Chat.css';
import ChatHeader from "./ChatHeader";
import app from "../firebaseConfig";
import {getFirestore, doc, setDoc} from "firebase/firestore";
const db = getFirestore(app);
function ChatScreen({Activechat}){
    

    const [inputMensaje, setInputMensaje] = useState("");

    function sendMessage(e){
        e.preventDefault();
        const docuRef =doc(db,`chat/${Activechat}/mensajes/${new Date().getTime()}`)
        setDoc(docuRef,{
            mensajes:inputMensaje,
            usuarios: "Ignacio"
        })

        setInputMensaje("");
    }
    
    return( <div className="chat">

        <ChatHeader />
        
        <div className="chat__messages">
            {/*Mappear mensajes de firebases*/}
        </div>
        
        <div className="chat__input">
           
           
                <form onSubmit={sendMessage}>
                    <input type="text" 
                    disabled={false} 
                    value={inputMensaje} 
                    onChange={(e)=>setInputMensaje(e.target.value)} 
                    placeholder="Type your message"
                    />

                    <button disabled={false} value={inputMensaje} className="chat__inputButton" type="submit">
                        Send Message
                    </button>

                </form>
            

            <div className="chat__inputIcons">
                
            </div>
            <link href="Chat.css"></link>
        </div>
    
    </div>
    )

}

export default ChatScreen;
