import React from "react";
import TimerView from "./TimerView";
import Buttons from "./Buttons";
import { setTimes, setStatus, resetSubCycle, setMsg, setMinutes} from "../../redux/slices/timer";
import { useDispatch, useSelector } from "react-redux";
import {configAlert,asynCall} from "./ConfigAlert";

import "../../App.css";
import { BackToStart } from "./TimerController";

function TimerFunctionality() {

  const dispatch = useDispatch();

  const { values: {status, msg} } = useSelector((state) => state.timer);

  const ConfigTimes = () => {
    const { studyTime, shortBreak, longBreak } = asynCall();
    
    //setSubCycleCount(0);
    resetSubCycle()
    //setMsg("Study Time");
    setMsg("Study Time")
    //setStatus(1);
    setStatus(1)

    if (status === 4) {
      BackToStart();
    }

setMinutes(studyTime)
      setTimes({
        studyTime: studyTime,
        shortBreak: shortBreak,
        longBreak: longBreak,
      })


    //setUserStudyTime(result.values.configStudyTime);
    //setActualMinutes(result.values.configStudyTime);
    //setUserShortBreak(result.values.configShortBreak);
    //setUserLongBreak(result.values.configLongBreak);
  };

  return (
    <div className="timerFunctionality">
      <span className="msg-text">{msg}</span>

      <TimerView/>

      <Buttons ConfigTimes={ConfigTimes} />
    </div>
  );
}

export default TimerFunctionality;
