import React, { useState } from 'react'
import Pomodoro from './components/Pomodoro'
import Buttons from './components/Buttons'
import Message from './components/Message'
//import ResetButton from './components/ResetButton'
import './App.css'

function App() {

  const initialMinutes = 0;
  const initialSeconds = 5;
  let breakMsg = 'Break Time';
  let studyMsg = 'StudyTime';

  const [time, setTime] = useState({m:initialMinutes, s:initialSeconds})
  const [msg, setMsg] = useState(studyMsg);
  const [interv, setInterv] = useState();
  const [status, setStatus] = useState(0);
  //Not started = 0
  //started = 1
  //stopped = 2
  //break = 3


  var updateM = time.m,
      updateS = time.s;

  const start = () => {
     run();
     setStatus(1);
     setInterv(setInterval(run, 250));
  }
      
  const run = () => {
    if(updateS === 0){
      if(updateM !== 0){
        updateS = 59;
        updateM--;
      } else{
        setTime({updateM:0 ,updateS:8});
        breakTime();
      }
      
    } else {
      updateS--;
    }
    return setTime({m:updateM, s:updateS});
  }

  const stop = () => {
    clearInterval(interv);
    setStatus(2);
  }

  const reset = () => {
    clearInterval(interv);
    setStatus(0);
    return setTime({m:initialMinutes, s:initialSeconds});
  }

  const resume = () => start();

  const breakRun = () =>{
    setInterv(setInterval(run, 250));
    setStatus(0);
    if(updateS === 0){
      if(updateM !== 0){
        updateS = 59;
        updateM--;
      } 
    } else {
      updateS--;
    }
    setTime({m:updateM, s:updateS});
  }

  const breakTime = () =>{
    clearInterval(interv);
    setStatus(3);
    //setTime({m:0 ,s:8});
    setMsg(breakMsg);
  }

  return (
    <div className='App'>
      <Message time={time} msg={msg}/>
      <Pomodoro time={time} status={status}/>
      <Buttons status={status} stop={stop} reset={reset} resume={resume} start={start} breakTime={breakTime} breakRun={breakRun} />
    </div>
  )
}

export default App

