import { StudyStart } from "../../components/Timer/TimerController";

const initialState = {
  values: {
    times: {},
    status: 0,
    subCycle: 0,
    cycle: 0,
    minutes: 0,
    seconds: 0,
    msg: "Set the timers to start",
  },
  error: null,
};

export const timerSlice = createSlice({
  name: "timer",
  initialState,
  reducers: {
      
StudyStart: (state, action) => {
  if (state.values.status === 1) {
