import React, { Fragment } from "react";
import SettingsIcon from "@mui/icons-material/Settings";
import {
  //StudyStart,
  ShortBreakStart,
  LongBreakStart,
  Stop,
  Resume,
  BackToStart,
  Skip,
} from "./TimerController";
import {
  decrementSeconds,
  decrementMinutes,
  incrementCycle,
  incrementSubCycle,
  setMinutes,
  setSeconds,
  setStatus,
  setMsg,
  resetSubCycle,
} from "../../redux/slices/timer";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

import "../../App.css";

function Buttons(props) {
  const {
    values: { status },
  } = useSelector((state) => state.timer);
  const dispatch = useDispatch();

  const StudyStart = () => {
    const {
      values: {
        status,
        seconds,
        minutes,
        times: { studyTime, shortBreak, longBreak },
        subCycle,
      },
    } = useSelector((state) => state.timer);

    if (status === 1) {
      incrementSubCycle();
      //updateSubCycleCount += 1;
      //setSubCycleCount(updateSubCycleCount);
      if (subCycle === 4) {
        incrementCycle();
        //updateCycleCount += 1;
        //setCycleCount(updateCycleCount);
      }
    }

    clearInterval();
    if (minutes === 0 && seconds === 0) {
      alert("Set study time");
    } else {
      StudyRun();
      setStatus(5);
      //setStatus(5);
      setInterval(StudyRun, 5);
    }
  };

  const StudyRun = () => {
    const dispatch = useDispatch();
    const {
      values: {
        seconds,
        minutes,
        times: { studyTime, shortBreak, longBreak },
        subCycle,
      },
    } = useSelector((state) => state.timer);

    if (seconds === 0) {
      if (minutes !== 0) {
        dispatch(decrementMinutes());
        //updateMinutes--;
        dispatch(setSeconds(59));
        //updateSeconds = 59;
        //setActualMinutes(updateMinutes);
        //setActualSeconds(updateSeconds);
      } else {
        dispatch(setMinutes(shortBreak));
        //setActualMinutes(userShortBreak);
        dispatch(setMsg("Short Break Time"));
        //setMsg("Short Break Time");
        dispatch(setStatus(2));
        //setStatus(2);
      }
      if (subCycle >= 3 && seconds === 0 && minutes === 0) {
        dispatch(setMsg("Long Break Time"));
        //setMsg("Long Break Time");
        dispatch(setStatus(3));
        //setStatus(3);
        clearInterval();
        dispatch(setMinutes(longBreak));
        //setActualMinutes(userLongBreak);
      }
    } else {
      //updateSeconds--;
      dispatch(decrementSeconds());
      //setActualMinutes(updateMinutes);
      //setActualSeconds(updateSeconds);
    }
  };

  return (
    <div className="btn-container">
      {status === 0 ? (
        <Fragment>
          <button className="btn-config" onClick={props.ConfigTimes}>
            <SettingsIcon
              className="btn-config"
              sx={{ fontSize: "45px", color: "white", marginTop: "5px" }}
            ></SettingsIcon>
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 1 ? (
        <Fragment>
          <button className="btn-functional" onClick={StudyStart}>
            Start Study
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 2 ? (
        <Fragment>
          <button className="btn-functional" onClick={ShortBreakStart}>
            Start Short Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 3 ? (
        <Fragment>
          <button className="btn-functional" onClick={LongBreakStart}>
            Start Long Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 4 ? (
        <Fragment>
          <button className="btn-functional" onClick={BackToStart}>
            Back to beginning
          </button>
          <button className="btn-config" onClick={props.ConfigTimes}>
            <SettingsIcon
              color="primary"
              sx={{ fontSize: "45px", color: "white" }}
            ></SettingsIcon>
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 5 ? (
        <Fragment>
          <button className="btn-functional" onClick={Stop}>
            Stop Study
          </button>
          <button className="btn-functional" onClick={Skip}>
            Skip to Short Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 6 && props.subCycleCount < 4 ? (
        <Fragment>
          <button className="btn-functional" onClick={Resume}>
            Resume Study
          </button>
          <button className="btn-functional" onClick={Skip}>
            Skip to Short Break
          </button>
        </Fragment>
      ) : status === 6 ? (
        <Fragment>
          <button className="btn-functional" onClick={Resume}>
            Resume Study
          </button>
          <button className="btn-functional" onClick={Skip}>
            Skip to Long Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 7 ? (
        <Fragment>
          <button className="btn-functional" onClick={Stop}>
            Stop Short Break
          </button>
          <button className="btn-functional" onClick={Skip}>
            Skip to Long Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 8 ? (
        <Fragment>
          <button className="btn-functional" onClick={Resume}>
            Resume Short Break
          </button>
          <button className="btn-functional" onClick={Skip}>
            Skip to Long Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 9 ? (
        <Fragment>
          <button className="btn-functional" onClick={Stop}>
            Stop Long Break
          </button>
        </Fragment>
      ) : (
        ""
      )}

      {status === 10 ? (
        <Fragment>
          <button className="btn-functional" onClick={Resume}>
            Resume Long Break
          </button>
        </Fragment>
      ) : (
        ""
      )}
    </div>
  );
}
export default Buttons;
