import React, { useState } from 'react'
import Pomodoro from './components/Pomodoro'
import Buttons from './components/Buttons'
import Message from './components/Message'
//import ResetButton from './components/ResetButton'
import './App.css'

function App() {

  let initialMinutes = 0;
  let initialSeconds = 5;

  const [time, setTime] = useState({m:initialMinutes, s:initialSeconds})
  const [msg, setMsg] = useState({b:'Break Time', s:'StudyTime'})
  const [interv, setInterv] = useState();
  const [status, setStatus] = useState(0);
  //Not started = 0
  //started = 1
  //stopped = 2
  //break = 3


  var updateM = time.m,
      updateS = time.s;

  const start = () => {
     run();
     setStatus(1);
     setInterv(setInterval(run, 1000));
  }
      
  const run = () => {
    if(updateS === 0){
      if(updateM !== 0){
        updateS = 59;
        updateM--;
      } else{
        breakTime();
      }
      
    } else {
      updateS--;
    }
    return setTime({m:updateM, s:updateS});
  }

  const stop = () => {
    clearInterval(interv);
    setStatus(2);
  }

  const reset = () => {
    clearInterval(interv);
    setStatus(0);
    return setTime({m:initialMinutes, s:initialSeconds});
  }

  const resume = () => start();

  const breakTime = () =>{
    clearInterval(interv);
    setStatus(3);
    setTime({m:5 ,s:0});
    if(status === 3){
      setStatus(0);
      setMsg(s);
    }
  }

  return (
    <div className='App'>
      <Message time={time} msg={msg}/>
      <Pomodoro time={time} status={status}/>
      <Buttons status={status} stop={stop} reset={reset} resume={resume} start={start} breakTime={breakTime}/>
    </div>
  )
}

export default App
