import React, { useState } from 'react'
import Pomodoro from './components/Pomodoro'
import Buttons from './components/Buttons'
import Message from './components/Message'
//import ResetButton from './components/ResetButton'
import './App.css'

function App() {

  const initialMinutes = 0;
  const initialSeconds = 5;
  let breakMsg = 'Break Time';
  let studyMsg = 'StudyTime';

  //const [time, setTime] = useState({m:initialMinutes, s:initialSeconds})
  const [minutes, setMinutes] = useState(initialMinutes);
  const [seconds, setSeconds] = useState(initialSeconds);
  const [msg, setMsg] = useState(studyMsg);
  const [interv, setInterv] = useState();
  const [status, setStatus] = useState(0);
  //Not started = 0
  //started = 1
  //stopped = 2
  //break = 3
  //back to study = 4
  //config = 5


  var updateM = minutes,
      updateS = seconds;

  const start = () => {
     run();
     setStatus(1);
     setInterv(setInterval(run, 250));
  }
      
  const run = () => {
    if(updateS === 0){
      if(updateM !== 0){
        updateS = 59;
        updateM--;
      } else{
        setStatus(3);
      }
      
    } else {
      updateS--;
    }
    setMinutes(updateM);
    setSeconds(updateS);
    //return setTime({m:updateM, s:updateS});
  }

  const stop = () => {
    clearInterval(interv);
    setStatus(2);
  }

  const reset = () => {
    clearInterval(interv);
    setStatus(0);
    setMinutes(initialMinutes);
    setSeconds(initialSeconds);
    //return setTime({m:initialMinutes, s:initialSeconds});
  }

  const resume = () => start();

  const run2 = () => {
    if(updateS === 0){
      if(updateM !== 0){
        updateS = 59;
        updateM--;
      } else{
        setStatus(4);
      }
      
    } else {
      updateS--;
    }
    setMinutes(updateM);
    setSeconds(updateS);
    //return setTime({m:updateM, s:updateS});
  }

  const breakRun = () =>{
    setInterv(setInterval(run2, 250));
    if(updateS === 0){
      if(updateM !== 0){
        updateS = 59;
        updateM--;
      }
    } else {
      updateS--;
    }
    setMinutes(updateM);
    setSeconds(updateS);
    //setTime({m:updateM, s:updateS});
  }

  const breakTime = () =>{
    clearInterval(interv);
    setMinutes(0);
    setSeconds(8);
    //setTime({m:0 ,s:8});
    setMsg(breakMsg);
  }

  const backToStudy = () => {
    clearInterval(interv);
    setMsg(studyMsg);
    reset();
  }

  const configTimer = () => {
    setStatus(5);
    if(status === 5){
      setStatus(0);
    }
  }

  const addTimer = () => {
    if(minutes >= 0 && minutes < 60){
      setMinutes(minutes + 5);
    } else {
      alert('Limite de tiempo de ciclo alcanzado');
    }

  }

  const subTimer = () => {
    if(minutes > 0){
      setMinutes(minutes - 5);
    } else {
      alert('No se puede restar')
    }

  }
  
  const backFromConfig = () => {
    setStatus(0);
  }

  return (
    <div className='App'>
      <Message msg={msg}/>
      <Pomodoro minutes={minutes} seconds={seconds} status={status}/>
      <Buttons status={status} stop={stop} reset={reset} resume={resume} start={start} 
      breakTime={breakTime} breakRun={breakRun} backToStudy={backToStudy} 
      configTimer={configTimer} sumar={addTimer} restar={subTimer} backFromConfig={backFromConfig}/>
    </div>
  )
}

export default App

