import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { useDispatch } from "react-redux";
import { getApp } from "firebase/app";
import { getFirestore, collection, onSnapshot } from "firebase/firestore";
import { StudyStart } from "../../components/Timer/TimerController";

const initialState = {
  values: {
    times: {},
    status: 0,
    subCycle: 0,
    cycle: 0,
    minutes: 0,
    seconds: 0,
    msg: "Set the timers to start",
  },
  error: null,
};

export const timerSlice = createSlice({
  name: "timer",
  initialState,
  reducers: {
    setTimes: (state, action) => {
      console.log("Entre a setTimes reducer");
      state.values.times = action.payload;
      console.log('studyTime: ' + state.values.times.studyTime)
      console.log('shortBreak: ' + state.values.times.shortBreak)
      console.log('longBreak: ' + state.values.times.longBreak)
    },
    setStatus: (state, action) => {
      console.log('entrando STATUS ' + state.values.status);
      state.values.status = action.payload;
      console.log('STATUS ' + state.values.status)
    },
    incrementSubCycle: (state) => {
      state.values.subCycle++;
    },
    incrementCycle: (state) => {
      state.values.cycle++;
    },
    resetSubCycle: (state) => {
      state.values.subCycle = 0;
    },
    setMsg: (state, action) => {
      state.values.msg = action.payload;
    },
    decrementMinutes: (state) =>{
        state.values.minutes--;
    },
    setMinutes: (state, action) =>{
        state.values.minutes = action.payload;
    },
    decrementSeconds: (state) =>{
        state.values.seconds--;
    },
    setSeconds: (state, action) => {
        state.values.seconds = action.payload;
    },
    runFunctionality: (state, action) => {
      if (action.payload.seconds === 0) {
        if (state.values.minutes !== 0) {
          decrementMinutes();
          //updateMinutes--;
          setSeconds(59);
          //updateSeconds = 59;
          //setActualMinutes(updateMinutes);
          //setActualSeconds(updateSeconds);
        } else {
          setMinutes(state.values.times.shortBreak);
          //setActualMinutes(userShortBreak);
          setMsg("Short Break Time");
          //setMsg("Short Break Time");
          setStatus(2);
          //setStatus(2);
        }
        if (state.values.subCycle >= 3 && state.values.seconds === 0 && state.values.minutes === 0) {
          setMsg("Long Break Time");
          //setMsg("Long Break Time");
          setStatus(3);
          //setStatus(3);
          clearInterval();
          setMinutes(state.values.times.longBreak);
          //setActualMinutes(userLongBreak);
        }
      } else {
        //updateSeconds--;
        decrementSeconds();
        //setActualMinutes(updateMinutes);
        //setActualSeconds(updateSeconds);
      }
    }
  },
});

export const {
  setTimes,
  setStatus,
  incrementCycle,
  incrementSubCycle,
  resetSubCycle,
  setMsg,
  decrementMinutes,
  setMinutes,
  decrementSeconds,
  setSeconds,
  runFunctionality
} = timerSlice.actions;
export default timerSlice.reducer;
