import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import {
  incrementCycle,
  incrementSubCycle,
  setStatus,
} from "../../redux/slices/timer";

const {
  value: { status, subCycle },
} = useSelector((state) => state.timer);

//const [actualMinutes, setActualMinutes] = useState(0);
//const [actualSeconds, setActualSeconds] = useState(0);

//const [interv, setInterv] = useState();

//  Study Time functions

const studyStart = () => {
  const dispatch = useDispatch();

  if (status === 1) {
    dispatch(incrementSubCycle());
    //updateSubCycleCount += 1;
    //setSubCycleCount(updateSubCycleCount);
    if (subCycle === 4) {
      dispatch(incrementCycle());
      //updateCycleCount += 1;
      //setCycleCount(updateCycleCount);
    }
  }
  clearInterval(interv);
  if (actualMinutes === 0 && actualSeconds === 0) {
    alert("Set study time");
  } else {
    studyRun();
    dispatch(setStatus(5));
    //setStatus(5);
    setInterv(setInterval(studyRun, 5));
  }
};

const studyRun = () => {
  const dispatch = useDispatch();

  if (updateSeconds === 0) {
    if (updateMinutes !== 0) {
      updateMinutes--;
      updateSeconds = 59;
      setActualMinutes(updateMinutes);
      setActualSeconds(updateSeconds);
    } else {
      setActualMinutes(userShortBreak);
      setMsg("Short Break Time");
      dispatch(setStatus(2));
      //setStatus(2);
    }
    if (subCycleCount >= 3 && updateSeconds === 0 && updateMinutes === 0) {
      dispatch(setMsg("Long Break Time"));
      //setMsg("Long Break Time");
      setStatus(3);
      clearInterval(interv);
      setActualMinutes(userLongBreak);
    }
  } else {
    updateSeconds--;
    setActualMinutes(updateMinutes);
    setActualSeconds(updateSeconds);
  }
};

//  Short Break functions

const shortBreakStart = () => {
  const dispatch = useDispatch();

  clearInterval(interv);
  dispatch(setStatus(7));
  //setStatus(7);
  shortBreakRun();
  setInterv(setInterval(shortBreakRun, 5));
};

const shortBreakRun = () => {
  const dispatch = useDispatch();

  if (updateSeconds === 0) {
    if (updateMinutes !== 0) {
      updateMinutes--;
      updateSeconds = 59;
      setActualMinutes(updateMinutes);
      setActualSeconds(updateSeconds);
    } else {
      setActualMinutes(userStudyTime);
      dispatch(setMsg("Study Time"));
      //setMsg("Study Time");
      dispatch(setStatus(1));
      //setStatus(1);
    }
  } else {
    updateSeconds--;
    setActualMinutes(updateMinutes);
    setActualSeconds(updateSeconds);
  }
};

// Long Break functions

const longBreakStart = () => {
  const dispatch = useDispatch();

  clearInterval(interv);
  dispatch(setStatus(9));
  //setStatus(9);
  longBreakRun();
  setInterv(setInterval(longBreakRun, 5));
};

const longBreakRun = () => {
  const dispatch = useDispatch();

  if (updateSeconds === 0) {
    if (updateMinutes !== 0) {
      updateMinutes--;
      updateSeconds = 59;
    } else {
      dispatch(setStatus(4));
      //setStatus(4);
    }
  } else {
    updateSeconds--;
  }
  setActualMinutes(updateMinutes);
  setActualSeconds(updateSeconds);
};

// General functions

const stop = () => {
  const dispatch = useDispatch();

  if (status === 5) {
    clearInterval(interv);
    dispatch(setStatus(6));
    //setStatus(6);
  } else if (status === 7) {
    clearInterval(interv);
    dispatch(setStatus(8));
    //setStatus(8);
  } else if (status === 9) {
    clearInterval(interv);
    dispatch(setStatus(10));
    //setStatus(10);
  }
};

const resume = () => {
  const dispatch = useDispatch();

  if (status === 6) {
    studyStart();
    dispatch(setStatus(5));
    //setStatus(5);
  } else if (status === 8) {
    shortBreakStart();
    dispatch(setStatus(7));
    //setStatus(7);
  } else if (status === 10) {
    longBreakStart();
    dispatch(setStatus(9));
    //setStatus(9);
  }
};

const backToStart = () => {
  const dispatch = useDispatch();

  dispatch(resetSubCycle());
  //setSubCycleCount(0);
  dispatch(setMsg("Study Time"));
  //setMsg("Study Time");
  setActualMinutes(userStudyTime);
  clearInterval(interv);
  if (status === 4) {
    dispatch(setStatus(1));
    //setStatus(1);
  }
};

const skip = () => {
  const dispatch = useDispatch();

  clearInterval(interv);
  if (status === 5 || (status === 6 && subCycleCount <= 3)) {
    dispatch(setStatus(2));
    //setStatus(2);
    dispatch(setMsg("Short Break Time"));
    //setMsg("Short Break Time");
    setActualMinutes(userShortBreak);
    setActualSeconds(0);
  } else if (status === 7 || status === 8 || (status === 6 && subCycle > 3)) {
    //setStatus(3);
    dispatch(setStatus(3));
    //setMsg("Long Break Time");
    dispatch(setMsg("Long Break Time"));
    setActualMinutes(userLongBreak);
    setActualSeconds(0);
  }
};

export {
  studyStart,
  studyRun,
  shortBreakStart,
  shortBreakRun,
  longBreakStart,
  longBreakRun,
  stop,
  resume,
  backToStart,
  skip,
};
