import React, {useState, useEffect, useRef} from "react";
import {useDispatch, useSelector} from 'react-redux'
import { FirebaseError, getApp } from "firebase/app";
import {
  getFirestore,
  collection,
  onSnapshot,
  deleteField,
  updateDoc,
  doc,
  setDoc,
  getDoc,
  arrayUnion,
  arrayRemove,
} from "firebase/firestore";

import { selectChatId, selectChatName} from "../redux/slices/ChatSlice";



import './Chat.css';
import ChatHeader from "./ChatHeader";



function ChatScreen(){
    
    
    const [inputMensaje, setInputMensaje] = useState(""); //para setear mensajes
    const [MessageList, setMessageList] = useState([]); //array con la collecion de mensajes
    const chatId = useSelector(selectChatId)
    const chatName = useSelector(selectChatName)
    const app = getApp()
    const db = getFirestore(app)
    //maneja el envio de mensajes guardandolos en MessageList
    function sendMessage(e){
        e.preventDefault();
        db.collection('chat').doc(chatId).collection('mensajes').add({
            timestamp: getFirestore.FieldValue.serverTimestamp(),
            message: inputMensaje,
            displayName: 'Ignacio'

        })
        
        
        
        
        setInputMensaje(" ")
        //console.log(message)
    }

    useEffect(()=>{
        const element = document.getElementById()
    })

   
    //guarda los mensajes en el local storage
    useEffect(()=>{
        if(chatId){
            db.collection('chat')
                .doc(chatId)
                .collection('mensajes')
                .orderBy('timestamp', 'asc')
                .onSnapshot((snapshot) =>
                    setMessageList(
                        snapshot.docs.map((doc)=>({
                            id: doc.id,
                            data: doc.data()
                        }))
                    )
                )

        }
        
    },[chatId])
    
    return( <div className="chat">

        <ChatHeader />
        
        
            
            {MessageList.map((id, data)=>(
                <React.Fragment key={id}>
                    <div className="chat_messages">
                        <span className="form-control bg-black btn mt-2" 
                        style={{textAlign: "left",fontWeight: "bold"}}>
                            {inputMensaje.user}: {data}
                        </span>
                    </div>
                </React.Fragment>
            ))}
        
        
        <div className="chat__input">
           
           
                <form onSubmit={sendMessage}>
                    <input type="text" 
                    disabled={false} 
                    value={inputMensaje} 
                    onChange={(e)=>setInputMensaje(e.target.value)} 
                    placeholder="Type your message"
                    />

                    <button disabled={false} value={inputMensaje} className="chat__inputButton" type="submit">
                        Send Message
                    </button>

                </form>
            

            <div className="chat__inputIcons">
               
                
            </div>
            <link href="Chat.css"></link>
        </div>
    
    </div>
    )

}

export default ChatScreen;
