import React, { useState } from 'react'
import Pomodoro from './components/Pomodoro'
import Buttons from './components/Buttons'
//import ResetButton from './components/ResetButton'
import './App.css'

function App() {

  let initialMinutes = 24;
  let initialSeconds = 5;
  const [time, setTime] = useState({m:initialMinutes, s:initialSeconds})
  const [interv, setInterv] = useState();
  const [status, setStatus] = useState(0);
  //Not started = 0
  //started = 1
  //stopped = 2


  var updateM = time.m,
      updateS = time.s;

  const start = () => {
     run();
     setStatus(1);
     setInterv(setInterval(run, 1000));
  }
      
  const run = () => {
    if(updateS === 0){
      if(updateM != 0){
        updateS = 59;
        updateM--;
      }
      
    } else {
      updateS--;
    }
    return setTime({m:updateM, s:updateS});
  }

  const stop = () => {
    clearInterval(interv);
    setStatus(2);
  }

  const reset = () => {
    clearInterval(interv);
    setStatus(0);
    return setTime({m:0, s:0});
  }

  const resume = () => start();

  return (
    <div className='App'>
      <Pomodoro time={time}/>
      <Buttons status={status} stop={stop} reset={reset} resume={resume} start={start}/>
    </div>
  )
}

export default App
