import React from 'react';

import './App.css';
import Auth from '../src/components/Auth';
import ChatScreen from '../src/components/Chat';
import {AddCircle, CreditCard, Gif, EmojiEmotions} from '@material-ui/icons'

function App() {
  return (
    <div className="chat">

        <div>Encabezado</div>

        <div className="chat_messages">
            {/*Mappear mensajes de firebases*/}
        </div>
        
        <div className="chat_input">
            <AddCircle fontSize="large" />
            <form>
                <input type="text" disabled value={inputMensaje} 
                onCHange={(e)=>setInputMensaje(e.target.value)} 
                placeholder="Enviar mensaje a #NombreCanal"
                />

                <button disabled clasName="chat_inputButton" type="submit">
                    Send Message
                </button>

            </form>

            <div className="chat_inputIcons">
                <CreditCard fontSize="large"/>
                <Gif fontSize="large"/>
                <EmojiEmotions fontSize="large"/>
            </div>

        </div>
    
    </div>
    
  );
}

export default App;
