import React from 'react'
import '../../App.css'
import { useSelector } from 'react-redux'
import {actualMinutes,actualSeconds} from './TimerController'

function TimerView() {

  const { value: {status, subCycle, cycle} } = useSelector((state) => state.timer);


return(
  <div className='view-container'>

    {(status !== 0) ?
    <div className='general-cycle'>
      <span>{'General Cycles: ' + cycle}</span>&nbsp;&nbsp;
    </div> : "" }

    {!(status === 0 || status === 9 || status === 10 || status === 4) ?
    <div className='sub-cycle'>
      <span>{'Sub Cycles: ' + subCycle}</span>&nbsp;&nbsp;
    </div> : "" }

    <div className='timer'>
      <span>{(actualMinutes) >= 10 ? actualMinutes : '0' + actualMinutes}</span>&nbsp;:&nbsp;
      <span>{(actualSeconds) >= 10 ? actualSeconds : '0' + actualSeconds}</span>&nbsp;
    </div>

  </div>
)

}

export default TimerView
