import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import {
  decrementSeconds,
  decrementMinutes,
  incrementCycle,
  incrementSubCycle,
  setMinutes,
  setSeconds,
  setStatus,
  setMsg,
} from "../../redux/slices/timer";

//const [actualMinutes, setActualMinutes] = useState(0);
//const [actualSeconds, setActualSeconds] = useState(0);

//const [interv, setInterv] = useState();

//  Study Time functions

const StudyStart = () => {
  const dispatch = useDispatch();
  const {
    value: {
      status,
      seconds,
      minutes,
      times: { studyTime, shortBreak, longBreak },
      subCycle,
    },
  } = useSelector((state) => state.timer);

  if (status === 1) {
    dispatch(incrementSubCycle());
    //updateSubCycleCount += 1;
    //setSubCycleCount(updateSubCycleCount);
    if (subCycle === 4) {
      dispatch(incrementCycle());
      //updateCycleCount += 1;
      //setCycleCount(updateCycleCount);
    }
  }
  clearInterval();
  if (minutes === 0 && seconds === 0) {
    alert("Set study time");
  } else {
    StudyRun();
    dispatch(setStatus(5));
    //setStatus(5);
    setInterval(StudyRun, 5);
  }
};

const StudyRun = () => {
  const dispatch = useDispatch();
  const {
    value: {
      seconds,
      minutes,
      times: { studyTime, shortBreak, longBreak },
      subCycle,
    },
  } = useSelector((state) => state.timer);

  if (seconds === 0) {
    if (minutes !== 0) {
      dispatch(decrementMinutes());
      //updateMinutes--;
      dispatch(setSeconds(59));
      //updateSeconds = 59;
      //setActualMinutes(updateMinutes);
      //setActualSeconds(updateSeconds);
    } else {
      dispatch(setMinutes(shortBreak));
      //setActualMinutes(userShortBreak);
      dispatch(setMsg("Short Break Time"));
      //setMsg("Short Break Time");
      dispatch(setStatus(2));
      //setStatus(2);
    }
    if (subCycle >= 3 && seconds === 0 && minutes === 0) {
      dispatch(setMsg("Long Break Time"));
      //setMsg("Long Break Time");
      dispatch(setStatus(3));
      //setStatus(3);
      clearInterval();
      dispatch(setMinutes(longBreak));
      //setActualMinutes(userLongBreak);
    }
  } else {
    //updateSeconds--;
    dispatch(decrementSeconds());
    //setActualMinutes(updateMinutes);
    //setActualSeconds(updateSeconds);
  }
};

//  Short Break functions

const ShortBreakStart = () => {
  const dispatch = useDispatch();
  const {
    value: {
      seconds,
      minutes,
      times: { studyTime, shortBreak, longBreak },
      subCycle,
    },
  } = useSelector((state) => state.timer);

  clearInterval();
  dispatch(setStatus(7));
  //setStatus(7);
  ShortBreakRun();
  setInterval(ShortBreakRun, 5);
};

const ShortBreakRun = () => {
  const dispatch = useDispatch();
  const {
    value: {
      seconds,
      minutes,
      times: { studyTime, shortBreak, longBreak },
      subCycle,
    },
  } = useSelector((state) => state.timer);

  if (seconds === 0) {
    if (minutes !== 0) {
      //updateMinutes--;
      dispatch(decrementMinutes());
      //updateSeconds = 59;
      dispatch(setSeconds(59));
      //setActualMinutes(updateMinutes);
      //setActualSeconds(updateSeconds);
    } else {
      //setActualMinutes(userStudyTime);
      dispatch(setMinutes(studyTime));
      dispatch(setMsg("Study Time"));
      //setMsg("Study Time");
      dispatch(setStatus(1));
      //setStatus(1);
    }
  } else {
    //updateSeconds--;
    dispatch(decrementSeconds());
    //setActualMinutes(updateMinutes);
    //setActualSeconds(updateSeconds);
  }
};

// Long Break functions

const LongBreakStart = () => {
  const dispatch = useDispatch();
  const {
    value: {
      seconds,
      minutes,
      times: { studyTime, shortBreak, longBreak },
      subCycle,
    },
  } = useSelector((state) => state.timer);

  clearInterval();
  dispatch(setStatus(9));
  //setStatus(9);
  LongBreakRun();
  setInterval(LongBreakRun, 5);
};

const LongBreakRun = () => {
  const dispatch = useDispatch();
  const {
    value: {
      seconds,
      minutes,
      times: { studyTime, shortBreak, longBreak },
      subCycle,
    },
  } = useSelector((state) => state.timer);

  if (seconds === 0) {
    if (minutes !== 0) {
      updateMinutes--;
      updateSeconds = 59;
    } else {
      dispatch(setStatus(4));
      //setStatus(4);
    }
  } else {
    dispatch(decrementSeconds());
    //updateSeconds--;
  }
  //setActualMinutes(updateMinutes);
  //setActualSeconds(updateSeconds);
};

// General functions

const Stop = () => {
  const dispatch = useDispatch();
  const {
    value: { status, subCycle },
  } = useSelector((state) => state.timer);

  if (status === 5) {
    clearInterval(interv);
    dispatch(setStatus(6));
    //setStatus(6);
  } else if (status === 7) {
    clearInterval(interv);
    dispatch(setStatus(8));
    //setStatus(8);
  } else if (status === 9) {
    clearInterval(interv);
    dispatch(setStatus(10));
    //setStatus(10);
  }
};

const Resume = () => {
  const dispatch = useDispatch();
  const {
    value: { status, subCycle },
  } = useSelector((state) => state.timer);

  if (status === 6) {
    studyStart();
    dispatch(setStatus(5));
    //setStatus(5);
  } else if (status === 8) {
    shortBreakStart();
    dispatch(setStatus(7));
    //setStatus(7);
  } else if (status === 10) {
    longBreakStart();
    dispatch(setStatus(9));
    //setStatus(9);
  }
};

const BackToStart = () => {
  const dispatch = useDispatch();
  const {
    value: { status, subCycle },
  } = useSelector((state) => state.timer);

  dispatch(resetSubCycle());
  //setSubCycleCount(0);
  dispatch(setMsg("Study Time"));
  //setMsg("Study Time");
  setActualMinutes(userStudyTime);
  clearInterval(interv);
  if (status === 4) {
    dispatch(setStatus(1));
    //setStatus(1);
  }
};

const Skip = () => {
  const dispatch = useDispatch();
  const {
    value: { status, subCycle },
  } = useSelector((state) => state.timer);

  clearInterval(interv);
  if (status === 5 || (status === 6 && subCycleCount <= 3)) {
    dispatch(setStatus(2));
    //setStatus(2);
    dispatch(setMsg("Short Break Time"));
    //setMsg("Short Break Time");
    setActualMinutes(userShortBreak);
    setActualSeconds(0);
  } else if (status === 7 || status === 8 || (status === 6 && subCycle > 3)) {
    //setStatus(3);
    dispatch(setStatus(3));
    //setMsg("Long Break Time");
    dispatch(setMsg("Long Break Time"));
    setActualMinutes(userLongBreak);
    setActualSeconds(0);
  }
};

export {
  StudyStart,
  StudyRun,
  ShortBreakStart,
  ShortBreakRun,
  LongBreakStart,
  LongBreakRun,
  Stop,
  Resume,
  BackToStart,
  Skip,
};
